import React, { useState, useEffect } from 'react';
import './App.css';

function App() {
  const [tasks, setTasks] = useState([]); // Estado para las tareas
  const [newTask, setNewTask] = useState(''); // Estado para una nueva tarea

  // Función para obtener las tareas desde el backend
  const fetchTasks = async () => {
    try {
      const response = await fetch('http://localhost:8080/api/tasks');
      if (!response.ok) {
        throw new Error(`Error al obtener tareas: ${response.statusText}`);
      }
      const data = await response.json();
      setTasks(data); // Actualiza el estado con las tareas obtenidas
    } catch (error) {
      console.error('Error fetching tasks:', error);
      alert('Hubo un problema al obtener las tareas. Verifica si el servidor está activo.');
    }
  };

  // Función para agregar una nueva tarea
  const addTask = async () => {
    if (!newTask.trim()) {
      alert('Por favor, ingresa un nombre para la tarea.');
      return;
    }

    try {
      const response = await fetch('http://localhost:8080/api/tasks', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ name: newTask }),
      });

      if (!response.ok) {
        throw new Error(`Error al agregar tarea: ${response.statusText}`);
      }

      const data = await response.json();
      setTasks((prevTasks) => [...prevTasks, data]); // Agrega la nueva tarea al estado
      setNewTask(''); // Limpia el campo de entrada
    } catch (error) {
      console.error('Error adding task:', error);
      alert('No se pudo agregar la tarea. Intenta de nuevo.');
    }
  };

  // Función para eliminar una tarea
  const deleteTask = async (id) => {
    try {
      const response = await fetch(`http://localhost:8080/api/tasks/${id}`, {
        method: 'DELETE',
      });

      if (!response.ok) {
        throw new Error(`Error al eliminar tarea: ${response.statusText}`);
      }

      setTasks((prevTasks) => prevTasks.filter((task) => task.id !== id)); // Elimina la tarea del estado
    } catch (error) {
      console.error('Error deleting task:', error);
      alert('No se pudo eliminar la tarea. Intenta de nuevo.');
    }
  };

  // Cargar las tareas al montar el componente
  useEffect(() => {
    fetchTasks();
  }, []);

  return (
    <div className="container">
      <h1>Lista de Tareas</h1>

      {/* Formulario para agregar nueva tarea */}
      <div className="task-input">
        <input
          type="text"
          value={newTask}
          onChange={(e) => setNewTask(e.target.value)}
          placeholder="Nueva tarea"
        />
        <button onClick={addTask}>Agregar tarea</button>
      </div>

      {/* Lista de tareas */}
      <ul className="task-list">
        {tasks.map((task) => (
          <li key={task.id} className="task-item">
            <span>{task.name}</span>
            <button className="delete" onClick={() => deleteTask(task.id)}>
              Eliminar
            </button>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default App;
