import React, { useState, useEffect } from 'react';
import './App.css';

function App() {
  const [tasks, setTasks] = useState([]);
  const [newTask, setNewTask] = useState('');
  const [newDescription, setNewDescription] = useState('');

  // Determina si estamos en un entorno Dockerizado o en desarrollo
  const apiBaseUrl = window.location.hostname === 'localhost' ? 'http://localhost:8080' : 'http://backend:8080';

  // Función para obtener las tareas
  const fetchTasks = async () => {
    try {
      const response = await fetch(`${apiBaseUrl}/api/tasks`);
      if (!response.ok) {
        throw new Error(`Error al obtener tareas: ${response.statusText}`);
      }
      const data = await response.json();
      setTasks(data.tasks);
    } catch (error) {
      console.error('Error fetching tasks:', error);
      alert('Hubo un problema al obtener las tareas.');
    }
  };

  // Función para agregar una nueva tarea
  const addTask = async () => {
    if (!newTask.trim() || !newDescription.trim()) {
      alert('Por favor, ingresa un nombre y una descripción para la tarea.');
      return;
    }

    try {
      const response = await fetch(`${apiBaseUrl}/api/tasks`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ taskName: newTask, description: newDescription }),
      });

      if (!response.ok) {
        throw new Error(`Error al agregar tarea: ${response.statusText}`);
      }

      // Recargar las tareas después de agregar una nueva
      fetchTasks();
      setNewTask('');
      setNewDescription('');
    } catch (error) {
      console.error('Error adding task:', error);
      alert('Hubo un problema al agregar la tarea.');
    }
  };

  // UseEffect para cargar las tareas al inicio
  useEffect(() => {
    fetchTasks();
  }, []);

  return (
    <div className="App">
      <h1>Lista de Tareas</h1>
      <div>
        <input
          type="text"
          placeholder="Nueva tarea"
          value={newTask}
          onChange={(e) => setNewTask(e.target.value)}
        />
        <input
          type="text"
          placeholder="Descripción"
          value={newDescription}
          onChange={(e) => setNewDescription(e.target.value)}
        />
        <button onClick={addTask}>Agregar Tarea</button>
      </div>
      <ul>
        {tasks.map((task, index) => (
          <li key={index}>
            <strong>{task.taskName}</strong>: {task.description}
          </li>
        ))}
      </ul>
    </div>
  );
}

export default App;
